import { store } from '../store';
import { devicesState } from '../reducers/devices';
import { pairingsState } from '../reducers/pairings';



/**
 * Get devices of all selected hubs
 * @return {HUB_DEVICES_MAP_TYPE}
 */
export function getDevices() {
