    changeState(state, action) {
      const newState = action.payload
      const oldState = state.state
      console.log ("User state " + oldState + " -> " + newState)
      switch (oldState) {
        case USER_STATES.WAITING_LANGUAGE: {
          if (newState === USER_STATES.LANGUAGE_SET) {
