              state.state = USER_STATES.WAITING_LOGIN;
            }
          }
          break;
        }
        case USER_STATES.WAITING_LOGIN: {
          if (newState === USER_STATES.LOGIN_DONE) {
            if (!isEmpty(state.authKey)) {
              if (isEmpty(state.eulaAcceted)) {
                state.state = USER_STATES.WAITING_EULA;
